import React from 'react';

class EventLoc extends React.Component {

    constructor(props) {
        super(props)
    }


    getEventmap(data) {
        const latitude = data[0]
        const longitude = data[1]
        const new_src = "https://maps.googleapis.com/maps/api/staticmap?center=" + latitude + "," + longitude + "&zoom=50&size=600x400&maptype=terrain&key=AIzaSyB-oQnkpiDtJiV7YX05PQxmHJviThfoa3o"

    }

    render = () => {
        if (this.props.coordinates) {
            getEventmap(this.props.coordinates)
            // TO DO #5: Display the actual details of the selected station
            //           instead of the placeholder text you see below. - DONE
            // <img src={asteroid_pic} width={100} height={125} alt="Ast_pic" />
            return (
                <img src={new_src} alt="event_loc" />
            )
        } else {
            return (
                <div className="col-sm-4">
                </div >
            )
        }
    }
}


export default EventLoc;
