import React from 'react';
import AsteroidInfo from './AsteroidInfo'


class AsteroidList extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            selectedAsteroidName: null,
            selectedAsteroidVelocity: null,
            selectedAsteroidminSize: null,
            selectedAsteroidmaxSize: null
        }

    }

    handleAsteroidClick = (e) => {
        e.preventDefault();
        const AsteroidIdentifier = e.currentTarget.getAttribute('data-asteroid-id')
        const selectedAsteroid = this.props.asteroids.find(asteroid => asteroid.id === AsteroidIdentifier)
        if (selectedAsteroid) {
            // TO DO #3: Update this component's state so that
            //           this.state.selectedStationName will contain 
            //           the selected station's name.
            console.log(selectedAsteroid)
            // Keep the following line. - DONE
            console.log(selectedAsteroid.close_approach_data)



            console.log(selectedAsteroid.name,
                //selectedAsteroid.close_approach_data.relative_velocity.kilometers_per_second,
                selectedAsteroid.estimated_diameter.meters.estimated_diameter_min,
                selectedAsteroid.estimated_diameter.meters.estimated_diameter_max)

            // this.setState({
            //     selectedAsteroidName: selectedAsteroid.name,
            //     selectedAsteroidVelocity: selectedAsteroid.close_approach_data.relative_velocity.kilometers_per_second,
            //     selectedAsteroidminSize: selectedAsteroid.estimated_diameter.meters.estimated_diameter_min,
            //     selectedAsteroidmaxSize: selectedAsteroid.estimated_diameter.meters.estimated_diameter_max
            // })


        }
    }



    render = () => {
        const AsteroidMarkup = (this.props.asteroids.length > 0 &&
            this.props.asteroids.map(asteroid => {
                return (
                    <li onClick={this.handleAsteroidClick} className="list-group-item" data-asteroid-id={asteroid.id} key={asteroid.id}>
                        <div className="d-flex justify-content-between align-items-center">
                            <p className="m-0">{asteroid.name}</p>
                        </div>
                    </li>
                )

            }
            )
        )
        return (
            <div className="row mt-5 justify-content-center">
                <div className="col-sm-5">
                    <ul className="list-group">
                        {AsteroidMarkup}
                    </ul>
                </div>
                <div className="col-sm-4">
                    <AsteroidInfo details={this.state.selectedStationDetails} name={this.state.selectedStationName} />
                </div>
            </div>
        )

    }
}

export default AsteroidList;
