import logo from './logo.svg';
import './App.css';
import React from 'react';
import NavBar from './NavBar'
import EventList from './EventList'
import AsteroidList from './AsteroidList'

class App extends React.Component {

    constructor(props) {
        super(props)
        this.state = {
            events: [],
            asteroids: []
        }
    }


    loadEvents = async (e) => {
        const httpResponse = await fetch("https://eonet.gsfc.nasa.gov/api/v3/events?status=all&start=2022-11-20&end=2022-11-28")
        const events = await httpResponse.json()
        console.log(events.events)
        this.setState({ events: events.events })
    }

    loadAsteroids = async (e) => {
        const httpResponse2 = await fetch("https://api.nasa.gov/neo/rest/v1/feed?start_date=2015-09-07&end_date=2015-09-08&api_key=r24grJqpanuO5fbtmuz0G8EicGxkUGEFeTUSVbKC")
        const asteroids = await httpResponse2.json()
        //console.log(asteroids.near_earth_objects)
        var asteroid_list = []
        for (var property in asteroids.near_earth_objects) {
            asteroid_list.push(String(property) + ": " + String(asteroids.near_earth_objects[property].length))
        }
        console.log(asteroid_list)
        this.setState({ asteroids: asteroid_list })
    }

    render() {

        return (
            <div className="container-fluid p-0">
                <NavBar onClickLoadAll={this.loadEvents} onClickLoadAsteroids={this.loadAsteroids} />
                <EventList events={this.state.events} />
                <AsteroidList events={this.state.asteroids} />
            </div>

        )
    }

}
export default App;
