import React from 'react';
import asteroid_pic from './asteroid.png'


class AsteroidInfo extends React.Component {

    constructor(props) {
        super(props)
    }

    render = () => {
        if (this.props.selectedAsteroidName) {
            // TO DO #5: Display the actual details of the selected station
            //           instead of the placeholder text you see below. - DONE
            return (
                <div className="border shadow p-5 text-center">
                    <h3> {this.props.selectedAsteroidName} </h3>
                    <img src={asteroid_pic} width={25} height={25} alt="Ast_pic" />;
                    <p>
                        <span className="badge bg-primary rounded-pill">
                            {this.props.selectedAsteroidminSize}
                        </span>
                        <span className="ps-4">Min diameter</span>
                    </p>
                    <p>
                        <span className="badge bg-primary rounded-pill">
                            {this.props.selectedAsteroidmaxSize}
                        </span>
                        <span className="ps-4">Max diameter</span>
                    </p>
                </div >
            )
        } else {
            return (
                <div className="col-sm-4">
                </div >
            )
        }
    }
}

export default AsteroidInfo;
